mapper.select:
SELECT goods_id, goods_name, goods_price, goods_description,
block_flag, create_time
FROM goods
WHERE 1 = 1

	AND goods_id = #{goodsId}


	AND goods_name = #{name}


	AND block_flag = #{blockFlag}


	ORDER BY ${rule}

mapper.update:
UPDATE goods

	goods_name = #{name}, goods_price = #{price}, goods_description = #{description}, block_flag = #{blockFlag}  

WHERE 1 = 1

	AND goods_id = #{goodsId}
