mapper.select:
select agent_id, goods_id, ifnull(sum(quantity), 0) AS quantity
FROM volume_item
WHERE 1 = 1 AND order_type='0'

    AND agent_id = #{agentId}


    AND goods_id = #{goodsId}


    AND date_format(create_time, '%Y-%m') = date_format(date_sub(curdate(), interval #{month} month), '%Y-%m');

select *from(
select agent_id, goods_id, sum(quantity) AS quantity,sum(price) AS price
FROM volume_item
WHERE 1 = 1 AND order_type='0'

    AND date_format(create_time, '%Y-%m') > date_format(date_sub(curdate(), interval #{totalMonth} month),
    '%Y-%m');

GROUP BY agent_id, goods_id) t

    AND agent_id = #{agentId}


    AND goods_id = #{goodsId}
;
