mapper.select:
SELECT order_item_id, order_item_status, goods_quantity, order_item_price, receive_address, customer_id,
goods_id, order_id,
block_flag, create_time
FROM order_item
WHERE 1 = 1

    AND order_item_id = #{orderItemId}


    AND order_item_status = #{status}


    AND customer_id = #{customerId}


    AND goods_id = #{goodsId}


    AND block_flag = #{blockFlag}

mapper.delete:
DELETE FROM order_item
WHERE order_item_id IN

    #{item.orderItemId}

mapper.select:
SELECT goods_id, goods_name, goods_price, goods_description, block_flag, create_time
FROM goods
WHERE goods_id = #{goodsId}

SELECT customer_id, customer_name, agent_id, block_flag, create_time
FROM customer
WHERE customer_id = #{customerId}
