mapper.select:
SELECT c.customer_id, c.customer_name, c.agent_id, ifnull(sum(order_item_price), 0) AS order_price, ifnull(count(order_id), 0) AS
deal_quantity
FROM order_item_sum ois RIGHT JOIN customer c on ois.customer_id = c.customer_id
WHERE 1 = 1 AND ois.order_type IN (0,2) AND ois.order_item_status IN (1,2,3)

    AND c.customer_id = #{customerId}


    AND c.agent_id = #{agentId}


    AND (c.customer_name LIKE BINARY #{search}
    OR c.agent_id IN (select agent_id FROM agent WHERE agent_name LIKE BINARY #{search})
    )

GROUP BY customer_id, customer_name

SELECT agent_id, agent_name, agent_phone
FROM agent
WHERE agent_id = #{agentId}
