mapper.select:
SELECT withdraw_id, bank_no, current_coffer, withdraw_amount, withdraw_status, block_flag, create_time, agent_id
FROM withdraw_record
WHERE 1 = 1

    AND withdraw_id = #{withdrawId}


    AND bank_no = #{bankCardNo}


    AND withdraw_status = #{status, typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


    AND agent_id = #{agentId}


    ORDER BY ${rule}

mapper.update:
UPDATE withdraw_record


        withdraw_status = #{status, typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler},


        block_flag = #{blockFlag},

    withdraw_id = #{withdrawId}

WHERE withdraw_id = #{withdrawId}

mapper.select:
SELECT agent_id, agent_name, agent_phone
FROM agent
WHERE agent_id = #{agentId}
